let o=[];const t=()=>{let t=$("#header").outerHeight(!0);$(".scroll-point").each(function(n){o[n]=Math.round(parseInt($(this).offset().top-t-10))})},n=()=>{let t=Math.round($(window).scrollTop());let n=$("#pc-nav li");$("#pc-nav li").removeClass("current");for(let s=o.length-1;s>=0;s--)if(t>=o[s]){$(n[s]).addClass("current");break}};$(window).scroll(()=>{t();n()}),$("#splash").delay(1500).fadeOut("slow",()=>{t();n()});